    <script>
        function validateBookingForm() {
            const equipmentIdInput = document.getElementById('equipmentIdInput');
            const equipmentId = equipmentIdInput ? equipmentIdInput.value : 0;

            if (!equipmentId || equipmentId <= 0) {
                alert('Error: Invalid equipment selected. Please refresh the page and try again.');
                return false;
            }

            // Validate dates
            const startDate = new Date(document.querySelector('input[name=""startDate""]').value);
            const endDate = new Date(document.querySelector('input[name=""endDate""]').value);

            if (!startDate || !endDate) {
                alert('Please select both start and end dates.');
                return false;
            }

            if (startDate >= endDate) {
                alert('End date must be after start date.');
                return false;
            }

            return true;
        }
    </script>
");
